import{_ as e,o as p,c as t,f as o}from"./app.c2c70b55.js";const h=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"posts/foreignfriend.md","lastUpdated":1670210692000}'),s={name:"posts/foreignfriend.md"},_=o("<p>\u5750\u5728\u529E\u516C\u5BA4\u7684\u65F6\u5019\u5076\u7136\u60F3\u5230\u4E86\u5728\u6FB3\u6D32\u5FF5\u4E66\u65F6\u5019\u7684\u4E00\u4E2A\u65E5\u672C\u5BA4\u53CB\u3002\u4E0D\u77E5\u9053\u4E3A\u4F55\u601D\u7EEA\u4F1A\u7A81\u7136\u98D8\u5230\u90A3\u91CC\u3002</p><p>\u5927\u6982\u53EA\u76F8\u5904\u4E861\u4E2A\u6708\u8FD8\u662F3\u4E2A\u6708\u7684\u65F6\u95F4\u6211\u4E0D\u592A\u8BB0\u5F97\u4E86\uFF0C\u4ED6\u4F4F\u5728\u6211\u9694\u58C1\u7684\u623F\u95F4\uFF0C\u65E9\u51FA\u665A\u5F52\u3002</p><p>\u4EC5\u5B58\u7684\u5370\u8C61\u4E2D\u4ED6\u662F\u4E00\u4E2A\u5F88\u5B89\u9759\u5F88\u793C\u8C8C\u7684\u7537\u751F\u3002\u76F8\u8C8C\u5DF2\u7ECF\u5341\u5206\u6A21\u7CCA\u4E86\uFF0C\u6BD4\u8F83\u7626\uFF0C\u4E2A\u5B50\u548C\u6211\u5DEE\u4E0D\u591A\uFF0C\u4E00\u526F\u6587\u8D28\u5F6C\u5F6C\u7684\u6837\u5B50\uFF1F</p><p>\u6211\u4FE9\u4E00\u8D77\u51FA\u8FC7\u4E00\u8D9F\u95E8\uFF0C\u4E00\u8D77\u5750\u7684\u7535\u8F66\uFF0C\u6211\u5E26\u4ED6\u53BB\u6211\u7684\u5B66\u6821\u8F6C\u4E86\u8F6C\uFF0C\u4E00\u8DEF\u4E0A\u6211\u4FE9\u4EA4\u6D41\u5012\u4E5F\u4E0D\u5C34\u5C2C\uFF0C\u76F8\u6BD4\u4E8E\u4ED6\u6211\u7684\u82F1\u6587\u8FD8\u7B97\u53EF\u4EE5\uFF0C\u4F46\u6211\u4E0D\u786E\u5B9A\u4ED6\u662F\u5426\u7406\u89E3\u6211\u8BF4\u7684\u8BDD\u3002</p><p>\u6211\u5E26\u4ED6\u53BB\u4E86\u5B66\u6821\u7684\u64CD\u573A\uFF0C\u73B0\u5728\u60F3\u60F3\u786E\u5B9E\u5F88\u602A\u5F02\uFF0C\u5F88\u5C11\u6709\u4EBA\u5E26\u670B\u53CB\u53BB\u5B66\u6821\u53C2\u89C2\u4E3B\u8981\u65F6\u95F4\u5446\u5728\u64CD\u573A\uFF0C\u4E5F\u8BB8\u662F\u6211\u5B66\u751F\u751F\u6DAF\u5927\u591A\u6570\u7F8E\u597D\u7684\u65F6\u5149\u90FD\u5728\u64CD\u573A\u4E0A\uFF0C\u4E5F\u8BB8\u662F\u770B\u5230\u7BEE\u7403\u573A\u548C\u8DD1\u9053\u8BA9\u6211\u89C9\u5F97\u719F\u6089\u3002\u4E00\u8DEF\u4E0A\u4ED6\u90FD\u5728\u62CD\u7167\uFF0C\u4E00\u5207\u5BF9\u4ED6\u6765\u8BF4\u90FD\u5F88\u65B0\u9C9C\uFF0C\u5F88\u5389\u5BB3\u3002</p><p>\u4E2D\u5348\uFF0C\u4ED6\u5E26\u6211\u53BB\u5403\u4E86\u4E00\u5BB6\u65E5\u6599\u5E97\uFF0C\u4ED6\u8BF4\u5F88\u6B63\u5B97\u3002\u5403\u7684\u662F\u5565\u4E5F\u4E0D\u592A\u8BB0\u5F97\u4E86\uFF0C\u53EF\u80FD\u662F\u62C9\u9762\u5427\u3002</p><p>\u6211\u4FE9\u5750\u8F66\u5230\u90A3\u5BB6\u5E97\u7684\u65F6\u5019\u5927\u698210\u70B9\u591A\uFF0C\u8FD8\u6CA1\u5F00\u95E8\uFF0C\u5C31\u5728\u95E8\u53E3\u7A0D\u7A0D\u7B49\u4E86\u4E00\u4F1A\u7B49\u5F00\u95E8\u3002\u65E5\u672C\u9910\u5385\u4E5F\u5165\u4E61\u968F\u4FD7\uFF0C\u4E60\u60EF\u4E86\u6FB3\u6D32\u4EBA\u7684\u4F5C\u606F\u3002</p><p>\u6211\u4EEC\u662F\u5F53\u5929\u6700\u65E9\u7684\u987E\u5BA2\uFF0C\u4E00\u8FDB\u95E8\u4ED6\u5C31\u719F\u7EC3\u7684\u64CD\u7740\u65E5\u8BED\u548C\u670D\u52A1\u5458\u70B9\u9910\uFF0C\u4E5F\u8BB8\u53EA\u6709\u5728\u8FD9\u91CC\u9762\u8BA9\u4ED6\u771F\u7684\u89C9\u5F97\u719F\u6089\u3002</p><p>\u4F9D\u7A00\u8BB0\u5F97\u4ED6\u8BF4\u4ED6\u662F\u5B66\u533B\u7684\uFF0C\u5B66\u533B\u4F3C\u4E4E\u5F88\u5E78\u82E6\uFF0C\u8981\u8BFB\u5F88\u591A\u5F88\u591A\u5E74\u4E66\u5427\u3002\u6211\u77E5\u9053\u65E5\u672C\u4EBA\u82F1\u8BED\u5411\u6765\u662F\u4E0D\u597D\u7684\uFF0C\u5411\u4ED6\u8FD9\u79CD\u4E13\u95E8\u8DD1\u6765\u6FB3\u6D32\u5B66\u4E60\u82F1\u6587\u7684\uFF0C\u5E94\u8BE5\u9700\u8981\u5F88\u5927\u52C7\u6C14\u3002\u6211\u5F88\u96BE\u60F3\u8C61\u4ED6\u662F\u600E\u4E48\u4ECE\u673A\u573A\u5750\u8F66\u6765\u5230homestay\u7684\uFF0C\u7136\u540E\u53C8\u662F\u600E\u4E48\u5EA6\u8FC7\u6BCF\u4E00\u5929\u7684\u3002</p><p>\u4ED6\u79BB\u5F00\u7684\u90A3\u5929\uFF0C\u6211\u7FFB\u4E86\u4E00\u5708\u81EA\u5DF1\u7684\u623F\u95F4\uFF0C\u60F3\u627E\u4E00\u4E2A\u7EAA\u5FF5\u54C1\u7ED9\u4ED6\u5E26\u56DE\u53BB\uFF0C\u6700\u540E\u53EA\u627E\u5230\u4E86\u4E00\u76D2\u8336\u53F6\uFF0C\u662F\u6211\u7238\u8BA9\u6211\u5E26\u7ED9\u6FB3\u6D32\u7684\u53D4\u53D4\u7684\u3002\u6211\u5E26\u7740\u8336\u53F6\u53BB\u9694\u58C1\u627E\u4ED6\u7684\u65F6\u5019\uFF0C\u4ED6\u6B63\u5728\u6536\u62FE\u884C\u674E\uFF0C\u6211\u628A\u8336\u53F6\u585E\u7ED9\u4ED6\uFF0C\u8BF4\u662F\u793C\u7269\uFF0C\u5C31\u8BF4chinese tea\uFF0C\u6211\u81EA\u5DF1\u4E0D\u559D\u8336\u53F6\uFF0C\u5982\u679C\u4ED6\u6CA1\u542C\u8BF4\u8FC7\u7684\u8BDD\u6211\u5927\u6982\u4E5F\u6CA1\u6CD5\u89E3\u91CA\u6E05\u695A\u4EC0\u4E48\u662F\u8336\u53F6\u3002\u4ED6\u5F88\u9707\u60CA\u7684\u63A5\u8FC7\u8FDE\u5FD9\u8BF4\u611F\u8C22\uFF0C\u7136\u540E\u8D76\u7D27\u56DB\u5904\u7FFB\u627E\uFF0C\u5927\u6982\u60F3\u627E\u4E2A\u4EC0\u4E48\u4E1C\u897F\u56DE\u793C\u7ED9\u6211\uFF0C\u4ED6\u7684\u884C\u674E\u5F88\u7B80\u5355\uFF0C\u6700\u540E\u53EA\u627E\u5230\u4E86\u4E00\u4E2A\u8FD8\u6CA1\u7528\u8FC7\u7684\u7C98\u6BDB\u5668\u7ED9\u6211\u3002\u6211\u5FC3\u60F3\u539F\u6765\u65E5\u672C\u4EBA\u4E5F\u7528\u8FD9\u73A9\u610F\u3002</p><p>\u8FD9\u4E2A\u7C98\u6BDB\u5668\u73B0\u5728\u8FD8\u5728\u6211\u8EAB\u8FB9\uFF0C\u6BCF\u6B21\u642C\u5BB6\u4E5F\u90FD\u5E26\u7740\u5B83\uFF0C\u6211\u89C9\u5F97\u603B\u80FD\u7528\u5F97\u4E0A\uFF0C\u53EF\u662F\u6211\u592A\u61D2\u4E86\uFF0C\u4ECE\u6765\u4E5F\u90FD\u4E0D\u6253\u7406\u81EA\u5DF1\u7684\u4E49\u52A1\uFF0C\u4E00\u76F4\u90FD\u6CA1\u62C6\u3002\u4E5F\u8BB8\u662F\u6211\u89C9\u5F97\u7528\u5B8C\u4E86\u4EE5\u540E\uFF0C\u6211\u7684\u4EBA\u751F\u4E2D\u53C8\u5C11\u4E86\u4E00\u4F4D\u65E5\u672C\u7684\u670B\u53CB\u3002</p><p>\u6211\u4E0D\u4E86\u89E3\u4ED6\uFF0C\u4ED6\u4E5F\u4E0D\u4E86\u89E3\u6211\uFF0C\u6211\u4EEC\u7684\u751F\u6D3B\u53EA\u5728\u90A3\u77ED\u6682\u7684\u65F6\u5149\u91CC\u76F8\u4EA4\u4E86\uFF0C\u4E4B\u540E\u518D\u4E5F\u4E0D\u4F1A\u8D70\u5230\u4E00\u8D77\u3002</p><p>\u73B0\u5728\u60F3\u60F3\u5176\u5B9E\u8FD9\u6837\u7684\u8FC7\u5BA2\u5728\u6211\u7684\u4EBA\u751F\u4E2D\u8FD8\u633A\u591A\u7684\uFF0C\u5F88\u591A\u4EBA\u6211\u90FD\u6CA1\u6709\u4E00\u70B9\u5370\u8C61\u4E86\uFF0C\u4E5F\u8BB8\u53EA\u4F1A\u5728\u67D0\u4E2A\u4F3C\u66FE\u76F8\u8BC6\u7684\u573A\u666F\u4E2D\u4F9D\u7A00\u56DE\u5FC6\u8D77\u6765\uFF0C\u8FC7\u540E\u53C8\u6D88\u6563\u4E0D\u89C1\u4E86\u3002</p>",13),a=[_];function n(r,c,i,d,f,l){return p(),t("div",null,a)}const T=e(s,[["render",n]]);export{h as __pageData,T as default};
